mapper.insert:
INSERT INTO files ( 
    id, file_name, file_path, file_size, 
    type, parent_table, parent_no
)
VALUES (
    #{id}, #{fileName}, #{filePath}, #{fileSize}, 
    #{type}, #{parentTable}, #{parentNo}
)

mapper.update:
UPDATE files
   SET file_name = #{fileName}
      ,file_path = #{filePath}
      ,file_size = #{fileSize}
      ,type = #{type}
      ,parent_table = #{parentTable}
      ,parent_no = #{parentNo}
WHERE id = #{id}

mapper.select:
SELECT * 
FROM files
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}

mapper.delete:
DELETE FROM files
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
