mapper.select:
 select * from inquiry 

where
   title LIKE CONCAT('%', #{keyword}, '%') and username=#{username}


where
   content LIKE CONCAT('%', #{keyword}, '%') and username=#{username}


where
   username LIKE CONCAT('%', #{keyword}, '%') and username=#{username}


where username=#{username}

 order by reg_date desc

mapper.update:
update inquiry
set title = #{title}
  , content = #{content}
  , upd_date = now()
  , type = #{type}

  , password=""


  , password = #{password}

where id = #{id}
