mapper.select:
SELECT m.*, f.no file_no, f.id file_id, f.fk_id, f.fk_table, f.division, f.url
FROM movie m join files f on m.id = f.fk_id
where m.id = #{id}

SELECT m.*, f.no file_no, f.id file_id, f.fk_id, f.fk_table, f.division, f.url
FROM movie m LEFT OUTER JOIN files f on m.id = f.fk_id
where m.id = #{id}

mapper.update:
UPDATE `movie`
   SET title = #{title}
      ,content = #{content}
      ,type = #{type}
      ,release_date = #{releaseDate}
      ,upd_date = now()
WHERE id = #{id}
